import React, { useEffect, useState } from 'react';
import Navigation from '../components/Navigation';
import { CopyToClipboard } from 'react-copy-to-clipboard';

const Contact = () => {
  const [isIsrael, setIsIsrael] = useState(null);

  useEffect(() => {
    const getLocation = () => {
      if (navigator.geolocation) {
        navigator.geolocation.getCurrentPosition((position) => {
          const { latitude, longitude } = position.coords;
          checkLocation(latitude, longitude);
        });
      } else {
        console.log('La géolocalisation n\'est pas prise en charge par votre navigateur.');
      }
    };

    getLocation();
  }, []);

  const checkLocation = (latitude, longitude) => {
    // Vérification des coordonnées d'Israël
    const israelCoordinates = {
      latitude: 31.771959,
      longitude: 35.217018
    };

    const distanceThreshold = 100; // Seuil de distance en kilomètres

    const distance = getDistance(latitude, longitude, israelCoordinates.latitude, israelCoordinates.longitude);
    const isInIsrael = distance <= distanceThreshold;

    setIsIsrael(!isInIsrael); // Inversion de la condition pour afficher "Bonjour" si la position n'est pas en Israël
  };

  const getDistance = (lat1, lon1, lat2, lon2) => {
    const R = 6371; // Rayon de la Terre en kilomètres
    const dLat = deg2rad(lat2 - lat1);
    const dLon = deg2rad(lon2 - lon1);
    const a =
      Math.sin(dLat / 2) * Math.sin(dLat / 2) +
      Math.cos(deg2rad(lat1)) * Math.cos(deg2rad(lat2)) *
      Math.sin(dLon / 2) * Math.sin(dLon / 2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    const distance = R * c;
    return distance;
  };

  const deg2rad = (deg) => {
    return deg * (Math.PI / 180);
  };

  const renderContactSection = () => {
    if (isIsrael) {
      return (
        <div className="contactBox">
          <h1>Contact-Me</h1>
          <ul>
            <li>
              <i className='fas fa-map-marker-alt'></i> 
              <span>Jerusalem</span>
            </li>
            <li>
              <i className='fas fa-mobile-alt'></i>
              <CopyToClipboard text="0539309450">
                <span className='clickInput' onClick={()=> {alert('Téléphone copié !');}}>053 930 9450</span>
              </CopyToClipboard>
            </li>
            <li>
              <i className='far fa-envelope'></i>
              <CopyToClipboard text="estherbergman16@gmail.com">
                <span className='clickInput' onClick={()=> {alert('E-mail copié !');}}>estherbergman16@gmail.com</span>
              </CopyToClipboard>
            </li>
          </ul>
        </div>
      );
    } else {
      return (
        <div className="contactBox">
          <h1>Bonjour</h1>
        </div>
      );
    }
  };


  return (
    <div className="contact">
      <Navigation />
      <div className="contactContent">
        <div className="header"></div>
        {renderContactSection()}
        <div className='socialNetwork'>
          <ul>
            <a href='https://www.linkedin.com/in/esther-elbaz-373725202/' target="_blank" rel="noopener noreferrer">
              <h4>LinkedIn</h4>
              <i className='fab fa-linkedin'></i>
            </a>
            <a href='https://github.com/estherberg?tab=repositories' target="_blank" rel="noopener noreferrer">
              <h4>github</h4>
              <i className='fab fa-github'></i>
            </a>
            <a href='https://api.whatsapp.com/send/?phone=%2B33695443075' target="_blank" rel="noopener noreferrer">
              <h4>whatsapp</h4>
              <i className='fab fa-whatsapp'></i>
            </a>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default Contact;
